
public class Average_3_Nums_Using_Functions {

	public static int Average(int a,int b,int c)
	{
		return (a+b+c)/3;
		 
	}
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("The average of the 3 numbers is = "+ Average( 5,6,7));
		
	}

}
